                voto = 75

                if voto >= 90:
                    giudizio = "Ottimo"
                elif voto >= 70:
                    giudizio = "Buono"
                elif voto >= 60:
                    giudizio = "Sufficiente"
                else:
                    giudizio = "Insufficiente"

                print(f"Voto {voto}: {giudizio}")

def fattoriale(n):
    if n == 0 or n == 1:
        return 1
    else:
        return n * fattoriale(n - 1)
            
risultato = fattoriale(5)
print(f"5! = {risultato}")
print(f"4! = {fattoriale(4)}")
